(ns oz.gk-test
  (:require [oz.core :as oz]))


(oz/start-plot-server!)

(def bar-chart
  {:width 600
   :data {:name "table",
          :values [{:category "A", :amount 28}
                   {:category "B", :amount 55}
                   {:category "C", :amount 43}
                   {:category "D", :amount 91}
                   {:category "E", :amount 81}
                   {:category "F", :amount 53}
                   {:category "G", :amount 19}
                   {:category "H", :amount 87}]}
   :mark "bar",
   :encoding {:x {:field "category", :type "ordinal"},
              :y {:field "amount", :type "quantitative"}}})

(oz/v! bar-chart)

(defn bpoints [n keyname]
  (take n (repeatedly #(into {} {; :x (rand-int 10)
                                 :y (rand-int 1000)
                                 keyname (case (rand-int 2)
                                           0 "true"
                                           1 "false")}))))


(defn gen-boxplot [n]
  {:width 400
   :data {:name "table"
          :values (bpoints n :set)}
   :mark {:type "boxplot"
          :extent 1.5}
   :encoding {
              :x {:field "set",
                  :type "ordinal"
                  :axis {:title (str "ABC: N=" (str n))}}
              :y {:field "y", :type "quantitative"}}})

(def boxplot (gen-boxplot 5000))

(oz/v! boxplot)

(oz/start-plot-server!)
; 8 seconds
(oz/v! (gen-boxplot 5000))





